import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);

  function fetchMoviesHandler() {
    //przechwytuje dane z serwera
    //wszystko wykonuje z funkcją .then() zeby miec gwarancje ze poprzednia rzecz sie wykonała zanim przejdzie dalej
    fetch("https://swapi.dev/api/films/").then(
      ((response) => {
        response.json(); //sciagam dane z json, to jest najpopularniejszy format danych
        //transformuje go do js
      }).then((data) => {
        //te dane pobrane muszą być przechowywane w stanie, który będzie updateowany i uzywany do MoviesList
        //dodalam stan
        //teraz array z bazy danych będzie nowym stanym
        const transformedMovies = data.results.map();
        setMovies(data.results);
      })
    );
  }

  return (
    <React.Fragment>
      <section>
        <button>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
