import { useState } from "react";

const BasicForm = (props) => {
  const [enteredFirstName, setEneteredFirstName] = useState("");
  const [enteredLastName, setEneteredLastName] = useState("");
  const [enteredEmail, setEneteredEmail] = useState("");

  const [enteredFirstNameIsTouched, setEneteredFirstNameIsTouched] =
    useState(false);
  const [enteredLastNameIsTouched, setEneteredLastNameIsTouched] =
    useState(false);
  const [enteredEmailIsTouched, setEneteredEmailIsTouched] = useState(false);

  const enteredFirstNameIsValid = enteredFirstName.trim() !== "";
  const eneterdFirstNameIsInvalid =
    !enteredFirstNameIsValid && enteredFirstNameIsTouched;
  const enteredLastNameIsValid = enteredLastName.trim() !== "";
  const eneterdLastNameIsInvalid =
    !enteredLastNameIsValid && enteredLastNameIsTouched;
  const enteredEmailIsValid = enteredEmail.includes("@");
  const eneterdEmailIsInvalid = !enteredEmailIsValid && enteredEmailIsTouched;

  let formIsValid = false;

  const onSubmitHandler = (event) => {
    event.preventDeafult();
    setEneteredFirstNameIsTouched(true);
    setEneteredLastNameIsTouched(true);
    setEneteredEmailIsTouched(true);
    if (formIsValid) {
      return;
      console.log(enteredFirstName);
    }

    setEneteredFirstName("");
    setEneteredLastName("");
    setEneteredEmail("");
  };

  const firstNameChangeHandler = (event) => {
    setEneteredFirstName(event.target.value);
  };
  const lastNameChangeHandler = (event) => {
    setEneteredLastName(event.target.value);
  };
  const emailChangeHandler = (event) => {
    setEneteredEmail(event.target.value);
  };

  if (
    enteredFirstNameIsValid &&
    enteredLastNameIsValid &&
    enteredEmailIsValid
  ) {
    formIsValid = true;
  }

  return (
    <form onSubmit={onSubmitHandler}>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            value={enteredFirstName}
            onChange={firstNameChangeHandler}
          />
          {eneterdFirstNameIsInvalid && <p>Error First Name</p>}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input
            type="text"
            id="name"
            value={enteredLastName}
            onChange={lastNameChangeHandler}
          />
          {eneterdLastNameIsInvalid && <p>Error Last Name</p>}
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input
          type="text"
          id="name"
          value={enteredEmail}
          onChange={emailChangeHandler}
        />
      </div>
      {eneterdEmailIsInvalid && <p>Error Email </p>}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
