import { useState } from "react";

const BasicForm = (props) => {
  const [enteredFirstName, setEnteredFirstName] = useState("");
  const [enteredLastName, setEnteredLastName] = useState("");
  const [enteredEmail, setEnteredEmail] = useState("");

  const [enteredFirstNameIsTouched, setEnteredFirstNameIsTouched] =
    useState(false);
  const [enteredLastNameIsTouched, setEnteredLastNameIsTouched] =
    useState(false);
  const [enteredEmailIsTouched, setEnteredEmailIsTouched] = useState(false);

  const enteredFirstNameIsValid = enteredFirstName.trim() !== "";
  const enteredFirstNameIsInvalid =
    !enteredFirstNameIsValid && enteredFirstNameIsTouched;
  const enteredLastNameIsValid = enteredLastName.trim() !== "";
  const enteredLastNameIsInvalid =
    !enteredLastNameIsValid && enteredLastNameIsTouched;
  const enteredEmailIsValid = enteredEmail.includes("@");
  const enteredEmailIsInvalid = !enteredEmailIsValid && enteredEmailIsTouched;

  let formIsValid = false;

  const onSubmitHandler = (event) => {
    event.preventDeafult();
    setEnteredFirstNameIsTouched(true);
    setEnteredLastNameIsTouched(true);
    setEnteredEmailIsTouched(true);
    if (!formIsValid) {
      console.log(enteredFirstName);
      return;
    }

    setEnteredFirstName("");
    setEnteredLastName("");
    setEnteredEmail("");
    setEnteredFirstNameIsTouched(false);
    setEnteredLastNameIsTouched(false);
    setEnteredEmailIsTouched(false);
  };

  const firstNameChangeHandler = (event) => {
    setEnteredFirstName(event.target.value);
  };
  const lastNameChangeHandler = (event) => {
    setEnteredLastName(event.target.value);
  };
  const emailChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };

  const firstNameBlurHandler = (event) => {
    setEnteredFirstNameIsTouched(true);
  };
  const lastNameBlurHandler = (event) => {
    setEnteredLastNameIsTouched(true);
  };

  const emailBlurHandler = (event) => {
    setEnteredEmailIsTouched(true);
  };
  if (
    enteredFirstNameIsValid &&
    enteredLastNameIsValid &&
    enteredEmailIsValid
  ) {
    formIsValid = true;
  }

  return (
    <form onSubmit={onSubmitHandler}>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            value={enteredFirstName}
            onChange={firstNameChangeHandler}
            onBlur={firstNameBlurHandler}
          />
          {enteredFirstNameIsInvalid && <p>Error First Name</p>}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input
            type="text"
            id="name"
            value={enteredLastName}
            onChange={lastNameChangeHandler}
            onBlur={lastNameBlurHandler}
          />
          {enteredLastNameIsInvalid && <p>Error Last Name</p>}
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input
          type="text"
          id="name"
          value={enteredEmail}
          onChange={emailChangeHandler}
          onBlur={emailBlurHandler}
        />
        {enteredEmailIsInvalid && <p>Error Email </p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
